import React, { useState } from 'react'

import './TimeCard.css'

const TimeCard = props => {
	const [time, setTime] = useState(Date.now())
	let timeInterval

	const startTime = () => {
		timeInterval = setInterval(() => setTime(Date.now()), 1000)
	}

	startTime()
    

	return (
		<div className='time-card'>
			<p className='time-card__time'>{Date.now.getSeconds()}</p>
		</div>
	)
}

export default TimeCard
