import React, { useState } from 'react'

import Header from './components/Header/Header'
import NewTODOButton from './components/NewTODOButton/NewTODOButton'
import NewTODOForm from './components/NewTODOForm/NewTODOForm'
import ListTODO from './components/ListTODO/ListTODO'

const DUMMY_TODO = [
	{
		id: 'e1',
		title: 'Fed cows',
		date: new Date(2022, 3, 26),
		category: 2,
		message: 'I have to fed cows!',
	},
	{ id: 'e2', title: 'Harvest a rice', date: new Date(2022, 2, 9), category: 1, message: 'This day I have to harvest the rice from over 25 hektares from fields in my farm!' },
	{
		id: 'e3',
		title: 'Fix harvester',
		date: new Date(2022, 8, 22),
		category: 3,
		message: 'I have to fix harvester, which was broken while harvesting rice!',
	},
]

const App = () => {
	const [todos, setTodos] = useState(DUMMY_TODO)
	const [isAddTodo, setIsAddTodo] = useState(false)

	const saveTodoDataHandler = enteredTodoData => {
		setTodos((prevTodo) => {
			return [enteredTodoData, ...prevTodo];
		  });
		setIsAddTodo(false)
	}

	const removeItemHandler = todoId => {
		setTodos(prevTodos =>{
			const updatedTodos = prevTodos.filter(todo)
		})
		setCourseGoals(prevGoals => {
		  const updatedGoals = prevGoals.filter(goal => goal.id !== goalId);
		  return updatedGoals;
		});
	  };

	const startAddingNewTodo = () => {
		setIsAddTodo(true)
	}

	const stopAddingNewTodo = () => {
		setIsAddTodo(false)
	}

	return (
		<div>
			<Header />
			{isAddTodo ? (
				<NewTODOForm onClose={stopAddingNewTodo} onSaveTodoData={saveTodoDataHandler} />
			) : (
				<NewTODOButton onAdd={startAddingNewTodo} />
			)}
			<ListTODO items={todos} onRemoveItem={removeItemHandler}/>
		</div>
	)
}

export default App
