import React from 'react'

import './Filter.css'

const Filter = (props) => {
    const [filteredOption, setFilteredOption] = React.useState(0)

    const selectedOptionHandler = option => {
		console.log(option)
	}

    return(
        <div className="filter">
            <div className="filter__title">Filter by:</div>
            <select className="filter__select">
                <option value="0" selected onChange={props.onSelected}>All Todos</option>
                <option value="1" onChange={selectedOptionHandler = () => 1}>Harvesting the crop</option>
                <option value="2" onChange={selectedOptionHandler = () => 2}>Animal care</option>
                <option value="3" onChange={selectedOptionHandler = () => 3}>Farm work</option>
            </select>
        </div>
    )
}

export default Filter
