import React, { useState } from 'react'

import './TimeCard.css'

const TimeCard = props => {
	const [date, setDate] = useState(Date.now())
	let timeInterval

	const startTime = () => {
		timeInterval = setInterval(() => setDate({ date: Date.now() }), 1000);
	}

	startTime()

	return (
		<div className='time-card'>
			<p className='time-card__time'></p>
		</div>
	)
}

export default TimeCard
